mapper.select:
SELECT host_ip AS hostIp, host_name AS hostName, host_desc AS hostDesc FROM host
AND host_ip = #{hostIp}
AND host_name = #{hostName}

mapper.delete:
DELETE

FROM host



		AND host_ip = #{hostIp}



		AND host_name = #{hostName}

mapper.update:
UPDATE
host
SET host_name =#{hostName},host_desc =#{hostDesc}
WHERE host_ip = #{hostIp}
